import axios from 'axios';
import React, { useState } from 'react';
import './SearchBar.scss';

const PAGE_BEERS = 'beers';
const PAGE_BASKET = 'basket'

const SearchBar = () => {

  const [page, setPage] = useState('beers');

  const [basket, setBasket] = useState([]);
  const [beers, setBeers] = useState([]);
  const [beerName, setBeerName] = useState('');

  const searchBeerFunc = () => {
    axios
      .get(`https://api.punkapi.com/v2/beers?beer_name=${beerName}`)
      .then((res) => {
        console.log(res.data);
        setBeers(res.data);
      });
  };

  const addToBasket = (beer) => {
    setBasket([...basket, beer]);
  };

  const navigateTo = (nextPage) => {
    setPage(nextPage)
  }

  return (
    <div>
      <button onClick={() => navigateTo(PAGE_BASKET)}>Go to basket ({basket.length})</button>
      <button onClick={() => navigateTo(PAGE_BEERS)}>Go to beers</button>
      <input
        onChange={(e) => {
          setBeerName(e.target.value);
        }}
        onKeyDown={searchBeerFunc}
        placeholder='search beer by name'
      ></input>
      <input placeholder='search beer by description'></input>
      {page === PAGE_BEERS && (
        <div className='beerContainer'>
          {beers.map((res, index) => (
            <div key={index} className='beerResults'>
              <p>{res.name}</p>
              <p>{res.tagline}</p>
              <img src={res.image_url} alt='beer_img' />
              <button
                onClick={() => {
                  addToBasket(res);
                }}
              >
                Add to basket
              </button>
            </div>
          ))}
        </div>
      )}
      {page === PAGE_BASKET && (
        <div className='beerContainer'>
                   <h1>Basket</h1>
          {basket.map((res, index) => (
            <div key={index} className='beerResults'>
              <p>{res.name}</p>
              <p>{res.tagline}</p>
              <img src={res.image_url} alt='beer_img' />
              <button
                onClick={() => {
                  addToBasket(res);
                }}
              >
                Add to basket
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
