import axios from 'axios'
import React, {useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import './SearchBar.scss'

const SearchBar = () => {

    const history = useHistory();

    const [page, setPage] = useState('beers')

    const [basket, setBasket] = useState([])
    const [beers, setBeers] = useState([])
    const [beerName, setBeerName] = useState('')


    const searchBeerFunc = () => {
        axios.get(`https://api.punkapi.com/v2/beers?beer_name=${beerName}`).
        then((res) => {
            console.log(res.data)
            setBeers(res.data)
        })
    }

    const addToBasket = (beer) => {
        setBasket([...basket, beer])
    }

    return (
        <div>
            <button>Go to cart ({basket.length})</button>
            <input
            onChange={(e) => {
                setBeerName(e.target.value)
            }}
            onKeyDown={searchBeerFunc}
            placeholder="search beer by name">
            </input>
            <input placeholder="search beer by description">
            </input>
            {page === 'beers' && (
            <div className="beerContainer">
            {beers.map((res, index) => (
                <div key={index} className="beerResults">
                <p>{res.name}</p>
                <p>{res.tagline}</p>
                <img src={res.image_url} alt="beer_img"/>
                <button onClick={() => {
                    addToBasket(res)
                }}>Add to basket</button>
                </div>
            ))}
            </div>
            )}
        </div>
    )
}

export default SearchBar
