import axios from 'axios'
import React, {useState, useEffect} from 'react'
import './SearchBar.scss'

const SearchBar = () => {

    const [beers, setBeers] = useState([])
    const [beerName, setBeerName] = useState('')

    // useEffect(() => {
    //     axios.get(`https://api.punkapi.com/v2/beers?beer_name=${beerName}`).
    //     then((res) => {
    //         console.log(res.data)
    //         setBeers(res.data)
    //     })
    // }, [])


    const searchBeerFunc = () => {
        axios.get(`https://api.punkapi.com/v2/beers?beer_name=${beerName}`).
        then((res) => {
            console.log(res.data)
            setBeers(res.data)
        })
    }

    return (
        <div>
            <input
            onChange={(e) => {
                setBeerName(e.target.value)
            }}
            onKeyDown={searchBeerFunc}
            placeholder="search beer by name">
            </input>
            <input placeholder="search beer by description">
            </input>
            {beers.map((res) => (
                <div className="beerResults">
                <h1>{res.name}</h1>
                <p>{res.tagline}</p>
                <img src={res.image_url} alt="beer_img"/>
                </div>
            ))}
        </div>
    )
}

export default SearchBar
